import React from 'react'
import { useState,useEffect } from 'react';
import axios from "../../../../api/axios"
import HeaderStudent from "../../HeaderStudent";
import GuideNav from "../../GuideNav";
import WebsiteFooter from "../../../general/WebsiteFooter";
import "../../PageContents.css";
import "./AddSupervisor.css";
import SupervisorCard from './SupervisorCard';
import ListCards from './ListCards';

const URL="Supervisor/";


const AddSupervisor = () => {
  const [card, setCard] = useState([]);
  const [searchResulte, setSearchResulte] = useState([]);
  const [errMsg, setErrMsg] = useState('');


  useEffect(() => {
    try {
      const response = axios.get(URL,{});
      setCard(response.data)
    }catch{
      if (!err?.response) {
        setErrMsg("No Server Response");
      }
    }
  }, []);
  
  const handleSearchChange = e => {
   
    const resultsArray = card.filter(card => card.Fullname.includes(e.target.value)
    || card.UserName.includes(e.target.value) ||card.Email.includes(e.target.value))
  
    setSearchResulte(resultsArray)
  
  };
  
  return (
    <div>
      <HeaderStudent />
      <div className="main-div">
        <div>
          <div className="form-box">
            <input 
            type="search" 
            id="search"
            className="search-for-Supervisor"
            onChange = {handleSearchChange}
            />
            <ListCards searchResulte ={searchResulte} />
            {errMsg}
            
            
          
          </div>
        </div>
        <GuideNav />
      </div>
      <WebsiteFooter />
    </div>

  )
}

export default AddSupervisor
