import React, {useContext}from "react";
import HeaderTrainingBody from "../HeaderTrainingBody";
import GuideNav from "../GuideNav";
import WebsiteFooter from "../../general/WebsiteFooter";
import "../PageContents.css";
import  TrainingBodyContex  from "../../../contex/TrainingBodyContex";


const AddTrainingOpp = () => {
  return (
    <div>
    <HeaderTrainingBody />
    <div className="main-div">
        <div>
          <form className="form-box" onSubmit={handleSubmit} >
          <label htmlFor="username" className="label-User">
              : اسم المستخدم
            </label>
            <input
              type="text"
              id="username"
              ref={userRef}
              className="username"
              onChange={(e) => setUser(e.target.value)}
              required
              placeholder="اسم المستخدم"
              onFocus={() => setUserFocus(true)}
            />
            <p
              className={
                userFocus && user && !validName ? "instructions" : "offscreen"
              }
            >
              يجب ان يكون اسم الامستخدم بين 4 - 24 و يكون <br />
              . بالغه الإنجليزية وان يبدأ بحرف
              <br />
            </p>
          </form>
        </div>
        <GuideNav />
      </div>
    <WebsiteFooter />
    </div>
  )
}

export default AddTrainingOpp
