import React from "react";
import { useRef, useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "../../general/general.css";
import "./RegisterStudent.css";
import imgTTF from "../../img/Taibah Training Platform.png";
import axios from "../../../api/axios";

const USER_REGEX = /^[a-zA-Z][a-zA-Z0-9-_]{3,24}$/;
const PASSWORD_REGEX = /^(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9]).{8,24}$/;
const REGISTER_URL = '/register';

const RegisterStudentBox = () => {
  const userRef = useRef();
  const errRef = useRef();

  const [user, setUser] = useState("");
  const [validName, setValidName] = useState(false);
  const [userFocus, setUserFocus] = useState(false);

  const [password, setPassword] = useState("");
  const [validPassword, setValidPassword] = useState(false);
  const [passwordFocus, setPasswordFocus] = useState(false);

  const [matchpassword, setMatchPassword] = useState("");
  const [validMatchPassword, setValidMatchPassword] = useState(false);
  const [matchPasswordFocus, setMatchPasswordFocus] = useState(false);

  const [errMag, setErrMag] = useState("");
  const [success, setSuccess] = useState(false);

  useEffect(() => {
    userRef.current.focus();
  }, []);

  useEffect(() => {
    setValidName(USER_REGEX.test(user));
    console.log(user);
  }, [user]);

  useEffect(() => {
    setValidPassword(PASSWORD_REGEX.test(password));
    const match = password === matchpassword;
    setValidMatchPassword(match);
    console.log(password);
    console.log(matchpassword);
  }, [password, matchpassword]);

  useEffect(() => {
    setErrMag("");
  }, [user, password, matchpassword]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log("user :"+user);
    console.log("password :"+password);
    console.log("matchpassword :"+matchpassword);
    setSuccess(true);
    errRef.current.focus();
    /*
        const respnse = await axios.post(REGISTER_URL,
            JSON.stringify({
            username : user, 
            password 
            }),
            {
            headers: { 'Content-Type': 'application/json' },
            withCredentials: true
            }
        );
        setSuccess(true)
    }catch (err) {
        if (!err?.response) {
            setErrMag('No Server Response');
        } else if (err.response?.status === 409) {
            setErrMag('Username Taken');
        } else {
            setErrMag('Registration Failed')
        }
        errRef.current.focus();
    }
    */
};

  return (
    <>
    {success ? (
         <div className="register-box">
            <img src={imgTTF} className="register-img" alt="imgTTF" />
                <div className="register-success">
                    <p className="p-success">
                    تم تسجيل الحساب بنجاح
                    </p>
                <Link to="/">
                <button className="submit_button">
                سجل الدخول
                </button>
                </Link>
                </div>
        </div>
      ) : (
        <div className="register-box">
            <img src={imgTTF} className="register-img" alt="imgTTF" />
            <p
                ref={errRef}
                className={errMag ? "errmag" : "offscreen"}
                aria-live="assertive"
                >
                {errMag}
            </p>
            <form onSubmit={handleSubmit} className="register-form">
                <label htmlFor="username" className="label-User">
                : اسم المستخدم
                </label>
                <input
                type="text"
                id="username"
                ref={userRef}
                className="username"
                onChange={(e) => setUser(e.target.value)}
                required
                placeholder="اسم المستخدم"
                aria-invalid={validName ? "false" : "true"}
                aria-describedby="uidnote"
                onFocus={() => setUserFocus(true)}
                />
                <p id="uidnote" className={userFocus && user && !validName ? "instructions" : "offscreen"}>
                            يجب ان يكون اسم الامستخدم بين 4 - 24<br />
                            Must begin with a letter.<br />
                            Letters, numbers, underscores, hyphens allowed.
                </p>
                <label htmlFor="password" className="label-password">
                    : كلمة المرور
                </label>
                <input
                type="password"
                className="password"
                id="password"
                onChange={(e) => setPassword(e.target.value)}
                aria-invalid={validPassword ? "false" : "true"}
                aria-describedby="pwdnote"
                onFocus={() => setPasswordFocus(true)}
                required
                placeholder="كلمة المرور"
                />
                <p id="pwdnote" className={passwordFocus && !validPassword ? "instructions" : "offscreen"}>
                8 to 24 characters.<br />
                Must include uppercase and lowercase letters, and number .<br />
                </p>
                <label htmlFor="confirm_password" className="label-confirm-password">
                    : تأكيد كلمة المرور 
                    </label>
                <input
                type="password"
                id="confirm_password"
                className="password"
                onChange={(e) => setMatchPassword(e.target.value)}
                required
                aria-invalid={validMatchPassword ? "false" : "true"}
                aria-describedby="confirmnote"
                onFocus={() => setMatchPasswordFocus(true)}
                placeholder="تأكيد كلمة المرور "
                />
                <p id="confirmnote" className={matchPasswordFocus && !validMatchPassword ? "instructions" : "offscreen"}>
                     Must match the first password input field.
                </p>
                <button className="submit_button"
                disabled={
                    !validName || !validPassword || !validMatchPassword
                    ? true
                    : false 
                }
                >
                تسجيل الحساب
                </button>
            </form>
          </div>
      )}
    </>
  );
};

export default RegisterStudentBox;
