import React, {useContext} from "react";
import HeaderSupervisor from "../../../HeaderSupervisor";
import GuideNav from "../../../GuideNav";
import WebsiteFooter from "../../../../general/WebsiteFooter";
import StudentContex from "../../../../../contex/StudentContex";
import "../Forms.css";

const MainPageForm = () => {

    const {StudentInfo} =useContext(StudentContex)

    const getData = async (e) => {
        try {
          const response = await axios.get(URL_SuperviseStudents, {});
          const Data = response.data.filter(
            (Supervise) => Supervise.Supervisor == SupervisorInfo.Id
          );
    
          const response2 = await axios.get(URL_Students, {});
          Data.map((Data) => {
            response2.data.map((student) => {
              if (student.id == Data.student) {
                if (!card.includes(student)) {
                  setCard2((prevState) => [...prevState, student]);
                }
              }
            });
          });
        } catch (err) {
          console.log(err.response);
        }
      };
    
      useEffect(() => {
        getData();
      }, []);
    


  return (
    <div>
      <HeaderSupervisor />
      <div className="main-div">
        <div className="Form-box">
          <div className="Students-info">
          <p className="p-Students-Card">
              : اسم المستخدم
              <br />
              {StudentInfo.UserName}
              <br />
              <br />
              : رقم الجوال
              <br />
              {StudentInfo.Phone_num}
            </p>

            <p className="p-Students-Card">
              : اسم الطالب
              <br />
              {StudentInfo.Fullname}
              <br />
              <br />
              : البريد الاكتروني
              <br />
              {StudentInfo.Email}
            </p>

          </div>
        </div>
        <GuideNav />
      </div>
      <WebsiteFooter />
    </div>
  );
};

export default MainPageForm;
