        id:isForm2id,
        student : StudentInfo.id,
        AcademicYear : AcademicYear,
        Semester: Semester,
        Major: Major,
        CGPA: CGPA,
        HoursEarned: HoursEarned,
        HoursRegistered: HoursRegistered,
        HoursRemaining: HoursRemaining,
        descriptionAboutCompany: descriptionAboutCompany,
        IsItCompleted: "true"
