          <form onSubmit={handleSubmit}>
            <FieldrForm
              label_name="اسم الفرصة التدريبية "
              ID="OpportName"
              label_class="label-OpportName"
              type="text"
              input_class="OpportName"
              placeholder=" ادخل اسم الفرصة التدريبية  "
              set={setOpportName}
            />
            <FieldrForm
              label_name="  "
              ID="OpportName"
              label_class="label-OpportName"
              type="text"
              input_class="OpportName"
