import React from "react";
import { useState, useContext, useEffect } from "react";
import "../../PageContents.css";
import "./Forms.css";
import axios from "../../../../api/axios";

const url_Students = "user/";

const StudentsCard = ({ card }) => {
  const [info, setinfo] = useState(true);

  const SuperviseStudents = async (e) => {
    try {
      const response = await axios.get(url_Students + card.UserName, {});
      setinfo(response.data);
    } catch (err) {
      console.log(err.response);
    }
  };
  const handleClick = (e) => {
    e.preventDefault();
    setOppInfo({
      Id: card.TrainingBody,
      Opport_name: card.Opport_name,
      Description: card.Description,
      Training_tasks: card.Training_tasks,
      Gender: card.Gender,
      Loaction: card.Loaction,
      Contact_Person: card.Position,
      Position: card.Position,
      vacancies: card.vacancies,
      StartDate: card.StartDate,
      FinishDate: card.FinishDate,
    })
    navigate("./OpportunityDetails");
  };
  useEffect(() => {
    SuperviseStudents();
  }, []);

  return (
    <div className="Students-Card">
      <button>
      <button className="button-Info-Students" onClick={handleClick}>
      للمزيد من المعلومات
      </button>
      </button>
      <p className="p-Students-Card">
        {" اسم الطالب : " + info.Fullname}
        <br />
        <br />
        {info.UserName + " : اسم المستخدم "}
        <br />
        <br />
        {info.Email + " : البريد الاكتروني  "}
      </p>
    </div>
  );
};

export default StudentsCard;
