import {  createContext, useState } from "react";


 const StudentContex = createContext();

export function StudentProvider({children}){
    const StorageInfo = localStorage.getItem('StudentInfo')
 ( StorageInfo ? JSON.parse(StorageInfo) : {
    UserName: "",
    Fullname: "",
    Phone_num: "",
    Email: "",
    Role: StorageInfo.Role,
    Uni_id: StorageInfo.Uni_id,
    Department: StorageInfo.Department,
    College: StorageInfo.College,
})
    const [StudentInfo,setStudentInfo] =useState({
        UserName: StorageInfo.UserName,
        Fullname: StorageInfo.Fullname,
        Phone_num: StorageInfo.Phone_num,
        Email: StorageInfo.Email,
        Role: StorageInfo.Role,
        Uni_id: StorageInfo.Uni_id,
        Department: StorageInfo.Department,
        College: StorageInfo.College,
    })

    useEffect(() => {
        localStorage.setItem('StudentInfo', JSON.stringify(StudentInfo))
      }, [StudentInfo])

    return(
        <StudentContex.Provider value={{ StudentInfo ,setStudentInfo}}>
            {children}
        </StudentContex.Provider>
    )
}
    


export default StudentContex;
