    try {
      const response = await axios.post(url, {

      });
      console.log(response)
      setsuccess(true)
    } catch (err) {
      console.log(err.response);
    }


  };

  const isAdded = ()=> {
    if(success){
    return( <button className="button-Add-Students" onClick={SuperviseStudents}>
       الاشراف على الطالب 
       </button>)
  }else{
    return <p> تمت اضافة الطالب بنجاح </p>
  }
  }


  return (
<div className="Students-Card">
  {}
<button className="button-Add-Students" onClick={SuperviseStudents}> الاشراف على الطالب </button>
<p className="p-Students-Card">
{" اسم الطالب : "+card.Fullname}  
<br />
<br />
   {card.UserName+" : اسم المستخدم "}
<br />
<br />
    {card.Email+" : البريد الاكتروني  "} 
</p>
</div>

  )
}

export default StudentsCard
