import {  createContext, useState, useEffect } from "react";


 const StudentContex = createContext();

export function StudentProvider({children}){
    
    const [StudentInfo,setStudentInfo] =useState({    
        UserName: "",
        Fullname: "",
        Phone_num: "",
        Email: "",
        Role: "",
        Uni_id: "",
        Department: "",
        College: "",
    })

    useEffect(() => {
        const data = localStorage.getItem("StudentInfo");
        if(data){
            console.log("run  ")
            setStudentInfo(JSON.parse(data));
        }
    },[]);

    useEffect(() => {
        localStorage.setItem("StudentInfo", JSON.stringify(StudentInfo))
        console.log("run  ")
      });

    return(
        <StudentContex.Provider value={{ StudentInfo ,setStudentInfo}}>
            {children}
        </StudentContex.Provider>
    )
}
    


export default StudentContex;
