import React, { Component } from "react";
import "./HeaderStyles.css";
import "./general.css";
import imgTaibah from "./img/Taibah Training Platform.png"



class Header extends Component {
     LoginButton() {
        window.location.replace("../LoginPage/Login.html");
    }
    
     RegisterButton() {
        location.replace("../registerPage/register.html");
    }
    
    //links = document.querySelectorAll(".header-button");
    //links.forEach(function (item) {
    //    item.addEventListener("click", function () {
    //        const element = document.getElementById(item.getAttribute("data-link"));
    //        element.scrollIntoView()
    //    })
    //})
    
  render() {
    return (
        <div class="header">
        <div class="header-lift">
          <div>
            <button className="login-button" onclick="LoginButton()">
              تسجيل دخول
            </button>
          </div>
          <div>
            <button className="register-button" onclick="RegisterButton()">
              حساب جديد
            </button>
          </div>
        </div>
        <div className="header-mid">
          <div>
            <button className="header-button" data-link="connect-with-us">
              اتصل بنا
            </button>
          </div>
          <div>
            <button className="header-button" data-link="View-training-opportunities">
              جهات التدريب
            </button>
          </div>
          <div>
            <button className="header-button" data-link="About-website">
              عن الموقع
            </button>
          </div>
          <div>
            <button className="header-button" data-link="Introduction">
              الرئيسية
            </button>
          </div>
        </div>
        <div className="header-right">
          <img src={imgTaibah} className="img-logo"/>
        </div>
      </div>

    )
  }
}

export default Header;
