import React from "react";
import { useRef, useState, useEffect } from "react";
import "../../general/general.css";
import imgTTF from "../../img/Taibah Training Platform.png";

const USER_REGEX = /^[a-zA-Z][a-zA-Z0-9-_]{3,24}$/;
const PASSWORD_REGEX = /^[a-zA-Z][a-zA-Z0-9-_]{3,24}$/;

const RegisterStudentBox = () => {
  const userRef = useRef();
  const errRef = useRef();

  const [user, setUser] = useState("");
  const [validName, setValidName] = useState(false);
  const [userFocus, setUserFocus] = useState(false);

  const [password, setPassword] = useState("");
  const [validPassword, setValidPassword] = useState(false);
  const [passwordFocus, setPasswordFocus] = useState(false);

  const [matchpassword, setMatchPassword] = useState("");
  const [validMatchPassword, setValidMatchPassword] = useState(false);
  const [matchPasswordFocus, setMatchPasswordFocus] = useState(false);

  const [errMag, setErrMag] = useState("");
  const [success, setSuccess] = useState(false);

  useEffect(() => {
    userRef.current.Focus();
  }, []);

  useEffect(() => {
    setValidName(USER_REGEX.test(user));
  }, [user]);

  useEffect(() => {
    setValidPassword(PASSWORD_REGEX.test(password));
    const match = password === matchpassword;
    setValidMatchPassword(match);
  }, [password, matchpassword]);

  useEffect(() => {
    setErrMag("");
  }, [user, password, matchpassword]);

  return (
    <div className="login-box">
      <img src={imgTTF} className="logo-img" alt="imgTTF" />
      <p ref={errRef} className={errMag ? "errmsg":"offscreen"}
        aria-live="assertive">
        {errmsg}
      </p>
      <form>
        <label htmlFor="username" className="username1">
          : اسم المستخدم
        </label>
        <input
          type="text"
          id="username"
          ref={userRef}
          className="username"
          onChange={(e) => setUser(e.target.value)}
          required
          placeholder="اسم المستخدم"
          aria-onInvalid={validName ? "false" : t}
        />
      </form>
      <div>
        <label htmlFor="password">: كلمة المرور</label>
        <input
          type="password"
          className="password"
          name="password"
          minLength="8"
          required
          placeholder="كلمة المرور"
        />
      </div>
      <input type="submit" value="تسجيل الدخول" />
    </div>
  );
};

export default RegisterStudentBox;
