import React from 'react';
import "../../PageContents.css";
import "./SearchForOpportunity.css";
import { Routes, Route } from "react-router-dom";
import { useNavigate  } from "react-router-dom";
import OpportunityDetails from "./OpportunityDetails"

const Opportunity = ({ card }) => {


  const navigate = useNavigate();

  const handleClick = (e) => {
    e.preventDefault();
    <Route path="/OpportunityDetails" element={<OpportunityDetails info={card} />} />
    navigate("./OpportunityDetails");
  }

  return (
<div className="Supervisor-Card">
<button className="button-Add-Supervisor" onClick={handleClick}>
   للمزيد من المعلومات </button>
<p className="p-Supervisor-Card">
{card.Opport_name+" : الفرصة التدريبة "}  
<br />
<br />
   {card.Description+" : الوصف "}
</p>
</div>

  )
}

export default Opportunity
