import React,{useState} from "react";
import axios from "../../../../api/axios";

const URL_AddOpportunity = "Opportunity/";

const ApplyCard = ({ apply }) => {
    const [success, setsuccess] = useState(true)

    const handleClick = async (e) => {

        e.preventDefault();
        try {
            await axios.put(URL_AddOpportunity+ apply.Opportunity +"/", {
            id: apply.id,
            TrainingBody: apply.TrainingBody,
            Opportunity: apply.Opportunity,
            Supervisor: apply.Supervisor,
            student: apply.student,
            IsItAccepted: "true"
            });
            setsuccess
          } catch (err) {
            console.log(err.response);
          }
        
      };

    const isAdded = ()=> {
        if(success){
        return( 
            <button className="button-Apply-Students" onClick={handleClick}>
            الموافقة على الطالب
          </button>
        )
      }else{
        return <p className="p-isAdded"> تمت نشر الاعلان </p>
      }
    }
  return (
    <div className="Apply-Students-Card">
        {isAdded}
      <p className="p-Apply-Students-Card">
        : اسم الفرصة
        <br />
        
        <br />
        <br />
        : وصف التدريب
        <br />
        
        <br />
        <br />
      </p>
    </div>
  );
};

export default ApplyCard;
