import React from "react";
import "../../general/general.css";
import "./RegisterStudent.css";

const fieldform = ({fieldname, CNameLabel, type, placeholder,PName,p,
    field, validField, fieldFocus,
    setField, setFieldFocus,userRef
}) => {

    return (
        <>
        <label htmlFor={fieldname} className={CNameLabel}>
              : اسم المستخدم
            </label>
            <input
              type={type}
              id={fieldname}
              ref={{fieldRef}}
              className={fieldname}
              onChange={(e) => setField(e.target.value)}
              required
              placeholder={placeholder}
              aria-invalid={validField ? "false" : "true"}
              aria-describedby={PName}
              onFocus={() => setFieldFocus(true)}
            />
            <p
              id={PName}
              className={
                fieldFocus && field && !validField ? "instructions" : "offscreen"
              }
            >
                {p}
            </p>
          
        </>
      );
}

export default fieldform;
