      </p>
      <p className="p-Students-Card">
      : اسم الطالب  
       <br />
        { info.Fullname}
        <br />
        <br />
        : مدى جدية التدريب 
       <br />
       <br />
       <br />
       : مدى الاستفادة من التدريب 
       <br />
      </p>
    </div>
  );
};

export default StudentsCard;
